import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';

const TileGrid = () => {
  const [activeTiles, setActiveTiles] = useState(new Set());

  const fetchActiveTiles = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/tiles');
      const data = await response.json();
      setActiveTiles(new Set(data));
    } catch (error) {
      console.error('Error fetching tiles:', error);
    }
  };

  useEffect(() => {
    fetchActiveTiles();
    const interval = setInterval(fetchActiveTiles, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Card className="w-full max-w-4xl mx-auto">
      <CardHeader>
        <CardTitle>Tile Activity Monitor</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-10 gap-2 p-4">
          {Array.from({ length: 100 }, (_, i) => i + 1).map(number => (
            <div
              key={number}
              className={`
                aspect-square
                flex
                items-center
                justify-center
                text-sm
                font-medium
                border
                rounded
                transition-colors
                duration-300
                ${activeTiles.has(number) 
                  ? 'bg-blue-500 text-white' 
                  : 'bg-white hover:bg-gray-100'
                }
              `}
            >
              {number}
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
};

export default TileGrid;
